mapper.select:
SELECT * FROM sys_user 
WHERE username = #{username} 
AND deleted = 0

SELECT * FROM sys_user 
WHERE id = #{id} 
AND deleted = 0

SELECT COUNT(*) FROM sys_user 
WHERE username = #{username} 
AND deleted = 0

mapper.update:
UPDATE sys_user
username = #{username}, real_name = #{realName}, phone = #{phone}, email = #{email}, gender = #{gender}, avatar = #{avatar}, status = #{status}, user_type = #{userType}, student_number = #{studentNumber}, department = #{department}, major = #{major}, class_name = #{className}, update_time = NOW()
WHERE id = #{id}

UPDATE sys_user 
SET deleted = 1, update_time = NOW()
WHERE id = #{id}

UPDATE sys_user 
SET password = #{password}, update_time = NOW()
WHERE id = #{id}

UPDATE sys_user 
SET status = #{status}, update_time = NOW()
WHERE id = #{id}

